/* Placeholder tiny Chart replacement - very small subset for demo only.
   For full features include official Chart.js from CDN or npm. */
window.Chart = function(ctx, config){
  // very minimal: draw labels as text
  const el = ctx.canvas;
  el.getContext('2d').fillStyle = '#333';
  el.getContext('2d').fillText(config.type + ' chart: ' + JSON.stringify(config.data.labels || []), 10, 20);
  return {};
};
